import React from 'react';
import { useLocation } from 'react-router-dom';
import './Results.css'; // Import your CSS file for styling

const Results = () => {
  const location = useLocation();
  const { favorites } = location.state || { favorites: { movies: '', books: '' } };

  // Sample recommended items (you can replace these with real data later)
  const recommendedMovies = [
    { title: 'Movie 1', img: 'path/to/movie1.jpg', details: 'Details about Movie 1' },
    { title: 'Movie 2', img: 'path/to/movie2.jpg', details: 'Details about Movie 2' },
  ];

  const recommendedBooks = [
    { title: 'Book 1', img: 'path/to/book1.jpg', details: 'Details about Book 1' },
    { title: 'Book 2', img: 'path/to/book2.jpg', details: 'Details about Book 2' },
  ];

  return (
    <div className="results-page">
      <div className="favorites">
        <h1 style={{ color: 'white' }}>Your Results</h1>
        <div className="favorites-content">
          <p style={{ color: 'white' }}><strong>Favorite Movie:</strong> {favorites.movies}</p>
          <p style={{ color: 'white' }}><strong>Favorite Book:</strong> {favorites.books}</p>
        </div>
      </div>

      <div className="recommendations">
        <h2 style={{ color: 'white' }}>Recommended Movies</h2>
        <div className="recommended-items">
          {recommendedMovies.map((movie, index) => (
            <div className="item" key={index}>
              <img src={movie.img} alt={movie.title} />
              <p>{movie.title}</p>
            </div>
          ))}
        </div>

        <h2 style={{ color: 'white' }}>Recommended Books</h2>
        <div className="recommended-items">
          {recommendedBooks.map((book, index) => (
            <div className="item" key={index}>
              <img src={book.img} alt={book.title} />
              <p>{book.title}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Results;
