import React, { useState, useEffect} from "react";
import { Link, useHistory } from "react-router-dom";
import "./Main.css";

function Main() {
  const history = useHistory();
  const [favorites, setFavorites] = useState({ movies: "", books: "" });
  const [recommendation, setRecommendation] = useState(null);
  const [isVisible, setIsVisible] = useState(false);
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFavorites((prevFavorites) => ({
      ...prevFavorites,
      [name]: value,
    }));
  };

  const getRecommendation = () => {
    // Mock recommendation result for now, or you can hook it up to backend
    
  const mockRecommendation = {
    movies: "An obscure tax document",
    books: "A guide to quantum physics",
  };
  setRecommendation(mockRecommendation);

    // Redirect to Results page with favorites as state
    history.push({
        pathname: '/results',
        state: { favorites }, // Passing favorites to the Results page
      });
    };
  // Use useEffect to trigger the slide-in effect on component mount
  useEffect(() => {
    const timer = setTimeout(() => {
        setIsVisible(true);
    }, 500); // Delay before sliding in

    return () => clearTimeout(timer);
}, []);
  return (
    <div className="main-page">
      <header className="hero">
      <div className={`slide-in ${isVisible ? "visible" : ""}`}>
        <h1>Welcome to The Anti-Algorithm</h1>
        <p>Your favorite reverse recommendation experience!</p>
        </div>
      </header>
     
      <div className="input-section">
        <h2>Enter Your Favorites</h2>
        <input
          type="text"
          name="movies"
          placeholder="Favorite Movies"
          value={favorites.movies}
          onChange={handleChange}
        />
        <input
          type="text"
          name="books"
          placeholder="Favorite Books"
          value={favorites.books}
          onChange={handleChange}
        />
        <button onClick={getRecommendation}>Surprise Me!</button>
      </div>

      {recommendation && (
        <div className="result-section">
          <h3>Your Surprise Recommendations:</h3>
          <p><strong>Movies:</strong> {recommendation.movies}</p>
          <p><strong>Books:</strong> {recommendation.books}</p>
        </div>
      )}

      <footer>
        <Link to="/about">Learn More About This Project</Link>
      </footer>
    </div>
  );
}

export default Main;
