import React from 'react';
import { useLocation } from 'react-router-dom';
import './Results.css'; // Import your CSS file for styling

const Results = () => {
  const location = useLocation();
  const { favorites, recommendation } = location.state || { 
    favorites: { movies: '', books: '' },
    recommendation: { movies: [], books: [] } // Set default recommendations
  };

  return (
    <div className="results-page">
      <div className="favorites">
        <h1 style={{ color: 'white' }}>Your Results</h1>
        <div className="favorites-content">
          <p style={{ color: 'white' }}><strong>Favorite Movie Genre:</strong> {favorites.movies}</p>
          <p style={{ color: 'white' }}><strong>Favorite Book Genre:</strong> {favorites.books}</p>
        </div>
      </div>

      <div className="recommendations">
        <h2 style={{ color: 'white' }}>Recommended Movies</h2>
        <div className="recommended-items">
          {recommendation.movies && recommendation.movies.length > 0 ? (
            recommendation.movies.map((movie, index) => (
              <div className="item" key={index}>
                <img src={movie.img} alt={movie.title} />
                <p>{movie.title}</p>
              </div>
            ))
          ) : (
            <p style={{ color: 'white' }}>No recommendations available for movies.</p>
          )}
        </div>

        <h2 style={{ color: 'white' }}>Recommended Books</h2>
        <div className="recommended-items">
          {recommendation.books && recommendation.books.length > 0 ? (
            recommendation.books.map((book, index) => (
              <div className="item" key={index}>
                <img src={book.img} alt={book.title} />
                <p>{book.title}</p>
              </div>
            ))
          ) : (
            <p style={{ color: 'white' }}>No recommendations available for books.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Results;
